package basics1;

public class Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10,r=20,p=30;
		if(i > p && i < r) {
			System.out.println(i + "is the greatest number");
		}
		if (r < p && r > i) {
			System.out.println(r + "is the greatest number" );
		}
		if (p > i && p > r) {
			System.out.println(p + "is the greatest number");} 
		}
	}


